import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_KEY = process.env.REACT_APP_TMDB_API_KEY;
const BASE_URL = 'https://api.themoviedb.org/3';

function App() {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] = useState('');

  useEffect(() => {
    fetchPopular();
  }, []);

  const fetchPopular = async () => {
    const res = await axios.get(`${BASE_URL}/movie/popular?api_key=${API_KEY}`);
    setMovies(res.data.results);
  };

  const searchMovies = async (e) => {
    e.preventDefault();
    if (!query) return fetchPopular();
    const res = await axios.get(`${BASE_URL}/search/movie?api_key=${API_KEY}&query=${query}`);
    setMovies(res.data.results);
  };

  return (
    <div style={{ fontFamily: 'sans-serif', background: '#181818', minHeight: '100vh', color: '#fff' }}>
      <h1 style={{ textAlign: 'center', color: '#e50914' }}>CineStream</h1>
      <form onSubmit={searchMovies} style={{ textAlign: 'center', marginBottom: 20 }}>
        <input
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search movies..."
          style={{ padding: 8, width: 250, borderRadius: 4, border: 'none' }}
        />
        <button type="submit" style={{ marginLeft: 10, padding: 8, borderRadius: 4, background: '#e50914', color: '#fff', border: 'none' }}>
          Search
        </button>
      </form>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {movies.map(movie => (
          <div key={movie.id} style={{ background: '#222', margin: 10, borderRadius: 8, width: 200, overflow: 'hidden' }}>
            {movie.poster_path && (
              <img src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`} alt={movie.title} style={{ width: '100%' }} />
            )}
            <div style={{ padding: 10 }}>
              <h3 style={{ margin: 0, fontSize: 18 }}>{movie.title}</h3>
              <p style={{ fontSize: 14, color: '#bbb' }}>{movie.release_date}</p>
              <p style={{ fontSize: 14 }}>{movie.overview?.slice(0, 100)}...</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
